// Create a new Intersection Observer
const observer = new IntersectionObserver((entries) => {
  console.log("entries",entries)
  entries.forEach((entry) => {
    if (entry.isIntersecting) {
      console.log("entry",entry)
      // Add the 'visible' class to the element
      entry.target.classList.add('visible');
      
      // Stop observing the element after it becomes visible
      observer.unobserve(entry.target);
    }
  });
});


// Select the element you want to apply the CSS styles to
const elements = document.querySelectorAll('.multicolumn-list__item .multicolumn-card__info');
elements.forEach((element,index) => {
  element.style['transition-delay'] = element.getAttribute("data-animation-delay") + 'ms'
  observer.observe(element);
});
  


